import React, { useState } from 'react';
import './App.css';

// Sample products
const products = [
  { id: 1, name: 'Mercury Acetaminophen 500mg', price: 8.99, category: 'OTC Medications' },
  { id: 2, name: 'Mercury Ibuprofen 200mg', price: 9.49, category: 'OTC Medications' },
  { id: 3, name: 'Mercury Aspirin 81mg', price: 7.99, category: 'OTC Medications' },
  { id: 4, name: 'Mercury Allergy Relief (Loratadine)', price: 12.99, category: 'OTC Medications' },
  { id: 5, name: 'Mercury Cough Syrup', price: 10.99, category: 'OTC Medications' },
];

const App = () => {
  const [cart, setCart] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const changeQuantity = (productId, amount) => {
    setCart(cart.map(item =>
      item.id === productId ? { ...item, quantity: item.quantity + amount } : item
    ));
  };

  const filteredProducts = selectedCategory === 'All' 
    ? products 
    : products.filter(product => product.category === selectedCategory);

  const subtotal = cart.reduce((total, product) => total + product.price, 0);
  const tax = subtotal * 0.12;
  const total = subtotal + tax;

  return (
    <div className="app">
      <div className="navbar">
        <div className="navbar-title">PharmaCare Drugstore POS</div>
        <div className="navbar-buttons">
          <button className="active">POS</button>
          <button>History</button>
        </div>
      </div>

      {/* Search bar */}
      <div className="search-bar">
        <input type="text" placeholder="Search products by name..." />
      </div>

      {/* Category Tabs */}
      <div className="category-tabs">
        <button onClick={() => setSelectedCategory('All')} className={selectedCategory === 'All' ? 'active' : ''}>All</button>
        <button onClick={() => setSelectedCategory('OTC Medications')} className={selectedCategory === 'OTC Medications' ? 'active' : ''}>OTC Medications</button>
        <button onClick={() => setSelectedCategory('Vitamins & Supplements')} className={selectedCategory === 'Vitamins & Supplements' ? 'active' : ''}>Vitamins & Supplements</button>
        <button onClick={() => setSelectedCategory('Personal Care')} className={selectedCategory === 'Personal Care' ? 'active' : ''}>Personal Care</button>
        <button onClick={() => setSelectedCategory('First Aid')} className={selectedCategory === 'First Aid' ? 'active' : ''}>First Aid</button>
        <button onClick={() => setSelectedCategory('Health & Wellness')} className={selectedCategory === 'Health & Wellness' ? 'active' : ''}>Health & Wellness</button>
        <button onClick={() => setSelectedCategory('Baby Care')} className={selectedCategory === 'Baby Care' ? 'active' : ''}>Baby Care</button>
      </div>

      {/* Product Display */}
      <div className="checkout-container">
        <div className="product-display">
          <h2>Products</h2>
          <div className="product-grid">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card">
                <img src="medicine.png" alt={product.name} className="product-image" />
                <div className="product-info">
                  <h4>{product.name}</h4>
                  <p>₱{product.price}</p>
                </div>
                <button className="add-to-cart" onClick={() => addToCart(product)}>+ Add to Cart</button>
              </div>
            ))}
          </div>
        </div>

        {/* Cart Sidebar */}
        <div className="cart-sidebar">
          <h2>Current Order</h2>
          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <div className="cart-items">
              {cart.map(item => (
                <div key={item.id} className="cart-item">
                  <div className="item-info">
                    <span>{item.name}</span>
                    <span>{item.quantity} x ₱{item.price}</span>
                  </div>
                  <div className="quantity-controls">
                    <button onClick={() => changeQuantity(item.id, 1)}>+</button>
                    <button onClick={() => changeQuantity(item.id, -1)}>-</button>
                  </div>
                  <button className="remove-btn" onClick={() => removeFromCart(item.id)}>Remove</button>
                </div>
              ))}
            </div>
          )}
          <div className="subtotal">
            <p>Subtotal: ₱{subtotal.toFixed(2)}</p>
            <p>Tax (12%): ₱{tax.toFixed(2)}</p>
            <p><strong>Total: ₱{total.toFixed(2)}</strong></p>
          </div>
          <button className="checkout-button">Proceed to Payment</button>
        </div>
      </div>
    </div>
  );
};

export default App;
