import React, { useState } from 'react';
import './App.css';

// Import Recharts components
import { 
  ResponsiveContainer, 
  AreaChart, 
  Area, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  PieChart, 
  Pie, 
  Cell, 
  Legend 
} from 'recharts';

// Import Product Images
import medicineImg from './assets/images/medicine.png';
import vitaminsImg from './assets/images/vitamins&supplements.png';
import personalCareImg from './assets/images/personalcare.png';
import firstAidImg from './assets/images/firstaid.png';
import healthWellnessImg from './assets/images/health&wellness.png';
import babyCareImg from './assets/images/babycare.png';

// Import Icons
import dashboard_CI from './assets/images/dashboard_CI.png';
import dashboard_NCI from './assets/images/dashboard_NCI.png';
import POS_CI from './assets/images/POS_CI.png';
import POS_NCI from './assets/images/POS_NCI.png';
import history_CI from './assets/images/history_CI.png';
import history_NCI from './assets/images/history_NCI.png';
import searchIcon from './assets/images/search_icon.png';
import deleteIcon from './assets/images/delete_icon.png';

// Import Dashboard Stat Icons
import total_revenue_icon from './assets/images/total_revenue_icon.png';
import transaction_icon from './assets/images/transaction_icon.png';
import avg_transaction from './assets/images/avg_transaction.png';
import items_sold_icon from './assets/images/items_sold_icon.png';

// Import Payment Icons
import cash_icon from './assets/images/cash_icon.png';
import card_icon from './assets/images/card_icon.png';
import mobile_icon from './assets/images/mobile_icon.png';

// --- Initial Mock Data ---
const initialTransactions = [
  { 
    id: 'TXN-1771607944136', 
    date: 'Feb 21, 2026',
    time: '1:19 AM', 
    amount: 12.31, 
    method: 'Mobile Payment',
    itemsCount: 1,
    items: [{ name: 'Cough Syrup', qty: 1, price: 10.99 }],
    subtotal: 10.99,
    tax: 1.32
  },
  { 
    id: 'TXN-1771607457636', 
    date: 'Feb 21, 2026',
    time: '1:10 AM', 
    amount: 70.50, 
    method: 'Credit/Debit Card',
    itemsCount: 2,
    items: [
        { name: 'Blood Pressure Monitor', qty: 1, price: 49.99 },
        { name: 'Compression Socks', qty: 1, price: 16.99 }
    ],
    subtotal: 66.98,
    tax: 3.52
  }
];

const revenueData = [
  { time: '8AM', amount: 0 }, { time: '9AM', amount: 15 }, { time: '11AM', amount: 45 },
  { time: '1PM', amount: 82 }, { time: '2PM', amount: 95 }, { time: '4PM', amount: 0 }
];

const categoryData = [
  { name: 'OTC Medications', value: 45 },
  { name: 'Vitamins', value: 25 },
  { name: 'Personal Care', value: 30 },
];

const App = () => {
  // State
  const [cart, setCart] = useState([]);
  const [localTransactions, setLocalTransactions] = useState(initialTransactions);
  const [searchQuery, setSearchQuery] = useState('');
  const [historySearch, setHistorySearch] = useState('');
  const [activeCategory, setActiveCategory] = useState('All');
  const [activeTab, setActiveTab] = useState('POS');
  const [expandedTxn, setExpandedTxn] = useState(null); 
  
  const [isPaymentModalOpen, setIsPaymentModalOpen] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState(null); 
  const [cashReceived, setCashReceived] = useState('');
  const [paymentStatus, setPaymentStatus] = useState('idle');

  const products = [
    { id: 1, name: 'Acetaminophen 500mg', price: 8.99, image: medicineImg, stock: 150, category: 'OTC Medications' },
    { id: 2, name: 'Ibuprofen 200mg', price: 9.49, image: medicineImg, stock: 200, category: 'OTC Medications' },
    { id: 5, name: 'Cough Syrup', price: 10.99, image: medicineImg, stock: 85, category: 'OTC Medications' },
    { id: 8, name: 'Multivitamin Daily', price: 15.99, image: vitaminsImg, stock: 140, category: 'Vitamins & Supplements' },
    { id: 14, name: 'Hand Sanitizer 8oz', price: 5.99, image: personalCareImg, stock: 220, category: 'Personal Care' },
    { id: 21, name: 'Adhesive Bandages (100ct)', price: 6.99, image: firstAidImg, stock: 150, category: 'First Aid' },
    { id: 27, name: 'Blood Pressure Monitor', price: 49.99, image: healthWellnessImg, stock: 35, category: 'Health & Wellness' },
    { id: 30, name: 'Compression Socks', price: 16.99, image: healthWellnessImg, stock: 80, category: 'Health & Wellness' },
    { id: 33, name: 'Baby Diapers (Size 3)', price: 24.99, image: babyCareImg, stock: 70, category: 'Baby Care' },
  ];

  const categories = ['All', 'OTC Medications', 'Vitamins & Supplements', 'Personal Care', 'First Aid', 'Health & Wellness', 'Baby Care'];

  // Calculations
  const subtotal = cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
  const tax = subtotal * 0.12;
  const total = subtotal + tax;

  const totalRevenue = localTransactions.reduce((acc, txn) => acc + txn.amount, 0);
  const totalItemsSold = localTransactions.reduce((acc, txn) => acc + txn.itemsCount, 0);

  // Handlers
  const filteredProducts = products.filter(p => 
    (activeCategory === 'All' || p.category === activeCategory) &&
    p.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const addToCart = (product) => {
    const exists = cart.find(item => item.id === product.id);
    if (exists) {
      setCart(cart.map(item => item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const updateQty = (id, delta) => {
    setCart(cart.map(item => item.id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item));
  };

  const handleCompletePayment = () => {
    const newTxn = {
      id: `TXN-${Date.now()}`,
      date: new Date().toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' }),
      time: new Date().toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' }),
      amount: total,
      method: paymentMethod === 'cash' ? 'Cash' : paymentMethod === 'card' ? 'Credit/Debit Card' : 'Mobile Payment',
      itemsCount: cart.reduce((acc, item) => acc + item.quantity, 0),
      items: cart.map(item => ({ name: item.name, qty: item.quantity, price: item.price })),
      subtotal: subtotal,
      tax: tax
    };

    setLocalTransactions([newTxn, ...localTransactions]);
    setPaymentStatus('success');
  };

  const closePaymentModal = () => {
    setIsPaymentModalOpen(false);
    setPaymentMethod(null);
    setCashReceived('');
    if (paymentStatus === 'success') {
        setCart([]);
        setPaymentStatus('idle');
    }
  };

  return (
    <div className="pos-container">
      <header className="main-nav">
        <h1 className="logo">PharmaCare Drugstore POS</h1>
        <div className="nav-actions">
          <button className={activeTab === 'Dashboard' ? 'nav-btn active' : 'nav-btn'} onClick={() => setActiveTab('Dashboard')}>
            <img src={activeTab === 'Dashboard' ? dashboard_CI : dashboard_NCI} alt="" /> Dashboard
          </button>
          <button className={activeTab === 'POS' ? 'nav-btn active' : 'nav-btn'} onClick={() => setActiveTab('POS')}>
            <img src={activeTab === 'POS' ? POS_CI : POS_NCI} alt="" /> POS
          </button>
          <button className={activeTab === 'History' ? 'nav-btn active' : 'nav-btn'} onClick={() => setActiveTab('History')}>
            <img src={activeTab === 'History' ? history_CI : history_NCI} alt="" /> History
          </button>
        </div>
      </header>

      {activeTab === 'Dashboard' && (
        <div className="dashboard-view">
          <div className="view-inner-container">
            <h2 className="dashboard-header-title">Dashboard Overview</h2>
            
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-info">
                  <h3>Total Revenue</h3>
                  <p className="stat-value">${totalRevenue.toFixed(2)}</p>
                  <p className="stat-subtext"> Today</p>
                </div>
                <div className="stat-icon-bg"><img src={total_revenue_icon} alt="" className="stat-img-icon" /></div>
              </div>
              <div className="stat-card">
                <div className="stat-info">
                  <h3>Transactions</h3>
                  <p className="stat-value">{localTransactions.length}</p>
                  <p className="stat-subtext">Total orders</p>
                </div>
                <div className="stat-icon-bg"><img src={transaction_icon} alt="" className="stat-img-icon" /></div>
              </div>
              <div className="stat-card">
                <div className="stat-info">
                  <h3>Avg. Transaction</h3>
                  <p className="stat-value">${localTransactions.length > 0 ? (totalRevenue / localTransactions.length).toFixed(2) : "0.00"}</p>
                  <p className="stat-subtext">Per order</p>
                </div>
                <div className="stat-icon-bg"><img src={avg_transaction} alt="" className="stat-img-icon" /></div>
              </div>
              <div className="stat-card">
                <div className="stat-info">
                  <h3>Items Sold</h3>
                  <p className="stat-value">{totalItemsSold}</p>
                  <p className="stat-subtext">Total units</p>
                </div>
                <div className="stat-icon-bg"><img src={items_sold_icon} alt="" className="stat-img-icon" /></div>
              </div>
            </div>

            <div className="dashboard-main-grid">
              <div className="content-card">
                <h3 className="card-title">Revenue by Hour</h3>
                <div style={{ width: '100%', height: 300 }}>
                  <ResponsiveContainer>
                    <AreaChart data={revenueData}>
                      <defs>
                        <linearGradient id="colorRev" x1="0" y1="0" x2="0" y2="1">
                          <stop offset="5%" stopColor="#5d7c5d" stopOpacity={0.3}/>
                          <stop offset="95%" stopColor="#5d7c5d" stopOpacity={0}/>
                        </linearGradient>
                      </defs>
                      <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#eee" />
                      <XAxis dataKey="time" axisLine={false} tickLine={false} tick={{fontSize: 12}} />
                      <YAxis axisLine={false} tickLine={false} tick={{fontSize: 12}} />
                      <Tooltip />
                      <Area type="monotone" dataKey="amount" stroke="#5d7c5d" fillOpacity={1} fill="url(#colorRev)" strokeWidth={3} />
                    </AreaChart>
                  </ResponsiveContainer>
                </div>
              </div>

              <div className="content-card">
                <h3 className="card-title">Sales by Category</h3>
                <div style={{ width: '100%', height: 300 }}>
                  <ResponsiveContainer>
                    <PieChart>
                      <Pie data={categoryData} cx="50%" cy="50%" outerRadius={80} dataKey="value" label={({ name }) => name}>
                        {categoryData.map((entry, index) => <Cell key={`cell-${index}`} fill={index === 0 ? "#5d7c5d" : "#a8bba8"} />)}
                      </Pie>
                      <Tooltip /><Legend verticalAlign="bottom" height={36}/>
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              </div>

              <div className="content-card">
                <h3 className="card-title">Recent Transactions</h3>
                <div className="recent-transactions-list">
                  {localTransactions.slice(0, 4).map((txn) => (
                    <div key={txn.id} className="transaction-card">
                      <div><p className="txn-id">{txn.id}</p><p className="txn-date">{txn.time}</p></div>
                      <div className="txn-info-right"><p className="txn-amount">${txn.amount.toFixed(2)}</p><p className="txn-method">{txn.method}</p></div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'History' && (
        <div className="history-view">
          <div className="view-inner-container">
            <h2 className="view-title">Transaction History</h2>
            <div className="history-search-container">
              <img src={searchIcon} alt="" className="search-icon-img" />
              <input 
                  type="text" 
                  className="modern-input"
                  placeholder="Search by transaction ID..." 
                  value={historySearch}
                  onChange={(e) => setHistorySearch(e.target.value)}
              />
            </div>

            <div className="history-accordion">
               {localTransactions.filter(t => t.id.toLowerCase().includes(historySearch.toLowerCase())).map(txn => (
                  <div key={txn.id} className={`history-card ${expandedTxn === txn.id ? 'expanded' : ''}`}>
                      <div className="history-card-header" onClick={() => setExpandedTxn(expandedTxn === txn.id ? null : txn.id)}>
                          <div className="header-left">
                              <div className="id-badge-row">
                                  <span className="txn-id-text">{txn.id}</span>
                                  <span className="method-pill">{txn.method}</span>
                              </div>
                              <p className="txn-meta-text">{txn.date}, {txn.time}  {txn.itemsCount} items</p>
                          </div>
                          <div className="header-right">
                              <span className="txn-total-text">${txn.amount.toFixed(2)}</span>
                              <span className={`chevron-icon ${expandedTxn === txn.id ? 'open' : ''}`}></span>
                          </div>
                      </div>
                      {expandedTxn === txn.id && (
                          <div className="history-card-body">
                              <p className="body-section-title">Items</p>
                              <div className="items-list">
                                {txn.items.map((item, idx) => (
                                    <div key={idx} className="item-detail-row">
                                        <div className="item-info">
                                            <p className="item-name-text">{item.name}</p>
                                            <p className="item-calc-text">${item.price.toFixed(2)}  {item.qty}</p>
                                        </div>
                                        <p className="item-price-sum">${(item.price * item.qty).toFixed(2)}</p>
                                    </div>
                                ))}
                              </div>
                              <div className="history-financial-summary">
                                  <div className="f-row"><span>Subtotal:</span> <span>${txn.subtotal.toFixed(2)}</span></div>
                                  <div className="f-row"><span>Tax:</span> <span>${txn.tax.toFixed(2)}</span></div>
                                  <div className="f-row f-total"><span>Total:</span> <span>${txn.amount.toFixed(2)}</span></div>
                              </div>
                          </div>
                      )}
                  </div>
               ))}
            </div>
          </div>
        </div>
      )}

      {activeTab === 'POS' && (
        <main className="pos-content">
          <div className="inventory-section">
            <div className="search-box">
              <img src={searchIcon} alt="" className="search-icon-img" />
              <input type="text" className="modern-input" placeholder="Search products..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
            </div>
            <div className="category-bar">
              {categories.map(cat => (
                <button key={cat} className={activeCategory === cat ? 'cat-btn active' : 'cat-btn'} onClick={() => setActiveCategory(cat)}>{cat}</button>
              ))}
            </div>
            <div className="product-grid">
              {filteredProducts.map(product => (
                <div key={product.id} className="product-card">
                  <div className="img-container"><img src={product.image} alt={product.name} /></div>
                  <h3 className="product-name">{product.name}</h3>
                  <p className="cat-label">{product.category}</p>
                  <div className="card-footer">
                      <div><span className="price">${product.price.toFixed(2)}</span><span className="stock">Stock: {product.stock}</span></div>
                      <button className="add-btn" onClick={() => addToCart(product)}>+</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
          <aside className="order-sidebar">
            <div className="sidebar-header"><h2 className="sidebar-title">Current Order</h2><button className="clear-all" onClick={() => setCart([])}>Clear All</button></div>
            <p className="item-count">{cart.length} items</p>
            <div className="cart-list">
              {cart.map(item => (
                <div key={item.id} className="cart-item">
                  <div className="item-details">
                    <h4 className="cart-item-name">{item.name}</h4>
                    <div className="qty-controls">
                      <button onClick={() => updateQty(item.id, -1)}>-</button><span>{item.quantity}</span><button onClick={() => updateQty(item.id, 1)}>+</button>
                    </div>
                  </div>
                  <div className="item-total-section">
                    <button className="delete-item" onClick={() => setCart(cart.filter(i => i.id !== item.id))}><img src={deleteIcon} alt="Delete" /></button>
                    <p className="item-total">${(item.price * item.quantity).toFixed(2)}</p>
                  </div>
                </div>
              ))}
            </div>
            <div className="billing-summary">
              <div className="bill-row"><span>Subtotal:</span> <span>${subtotal.toFixed(2)}</span></div>
              <div className="bill-row"><span>Tax (12%):</span> <span>${tax.toFixed(2)}</span></div><hr />
              <div className="bill-row total"><span>Total:</span> <span>${total.toFixed(2)}</span></div>
              <button className="pay-btn" disabled={cart.length === 0} onClick={() => setIsPaymentModalOpen(true)}>Proceed to Payment</button>
            </div>
          </aside>
        </main>
      )}

      {isPaymentModalOpen && (
        <div className="modal-overlay">
          {paymentStatus === 'success' ? (
            <div className="success-modal">
              <div className="success-icon"></div><h2 className="modal-title">Payment Successful!</h2><p>Transaction completed</p>
              <button className="close-success-btn" onClick={closePaymentModal}>Back to POS</button>
            </div>
          ) : (
            <div className="payment-modal">
              <div className="modal-header"><h2 className="modal-title">Payment</h2><button className="close-modal" onClick={closePaymentModal}></button></div>
              <div className="amount-display"><p>Total Amount</p><h1 className="total-h1">${total.toFixed(2)}</h1></div>
              {!paymentMethod ? (
                <div className="payment-options">
                  <p className="section-label">Select Payment Method</p>
                  <div className="method-grid">
                    <button className="method-item" onClick={() => setPaymentMethod('cash')}><img src={cash_icon} alt="" className="method-img-icon" /><span>Cash</span></button>
                    <button className="method-item" onClick={() => setPaymentMethod('card')}><img src={card_icon} alt="" className="method-img-icon" /><span>Card</span></button>
                    <button className="method-item" onClick={() => setPaymentMethod('mobile')}><img src={mobile_icon} alt="" className="method-img-icon" /><span>Mobile</span></button>
                  </div>
                </div>
              ) : paymentMethod === 'cash' ? (
                <div className="cash-payment-view">
                  <div className="view-header"><p className="section-label">Cash Payment</p><button className="change-method" onClick={() => setPaymentMethod(null)}>Change Method</button></div>
                  <div className="input-group">
                    <label className="modern-label">Cash Received</label>
                    <input type="number" className="modern-input-lg" placeholder="0.00" value={cashReceived} onChange={(e) => setCashReceived(e.target.value)} />
                  </div>
                  <div className="modal-actions">
                    <button className="cancel-btn" onClick={closePaymentModal}>Cancel</button>
                    <button className={`complete-btn ${parseFloat(cashReceived) >= total ? 'active' : ''}`} disabled={parseFloat(cashReceived) < total || !cashReceived} onClick={handleCompletePayment}>Complete Payment</button>
                  </div>
                </div>
              ) : (
                <div className="payment-method-view">
                  <div className="view-header"><p className="section-label">{paymentMethod === 'card' ? 'Card Payment' : 'Mobile Payment'}</p><button className="change-method" onClick={() => setPaymentMethod(null)}>Change Method</button></div>
                  <div className="reader-status-box">
                    <img src={paymentMethod === 'card' ? card_icon : mobile_icon} alt="" className="status-img-icon" />
                    <p className="main-prompt">Waiting for interaction...</p>
                  </div>
                  <div className="modal-actions"><button className="cancel-btn" onClick={closePaymentModal}>Cancel</button><button className="complete-btn active" onClick={handleCompletePayment}>Complete Payment</button></div>
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
