import React, { useState } from 'react';
import './App.css';

function App() {
  const [showCart, setShowCart] = useState(false);
  const [activeButton, setActiveButton] = useState('POS');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const products = {
    'OTC Medications': [
      { name: 'Mercury Acetaminophen 500mg', price: 8.99, stock: 150 },
      { name: 'Mercury Ibuprofen 200mg', price: 9.49, stock: 200 },
      { name: 'Mercury Aspirin 81mg', price: 7.99, stock: 180 },
      { name: 'Mercury Allergy Relief (Loratadine)', price: 12.99, stock: 120 },
      { name: 'Mercury Cough Syrup', price: 10.99, stock: 85 },
    ],
    'Vitamins & Supplements': [
      { name: 'Multivitamin Daily', price: 15.99, stock: 140 },
      { name: 'Vitamin D3 2000 IU', price: 12.99, stock: 110 },
      { name: 'Vitamin C 1000mg', price: 13.49, stock: 130 },
      { name: 'Omega-3 Fish Oil', price: 18.99, stock: 90 },
      { name: 'Calcium + Vitamin D', price: 14.99, stock: 105 },
    ],
    'Personal Care': [
      { name: 'Hand Sanitizer 8oz', price: 5.99, stock: 220 },
      { name: 'Toothpaste Whitening', price: 6.49, stock: 180 },
      { name: 'Mouthwash Antiseptic', price: 7.99, stock: 140 },
      { name: 'Dental Floss', price: 3.99, stock: 200 },
      { name: 'Body Lotion 16oz', price: 9.99, stock: 100 },
    ],
    'First Aid': [
      { name: 'Adhesive Bandages (100ct)', price: 6.99, stock: 150 },
      { name: 'Gauze Pads Sterile', price: 8.49, stock: 110 },
      { name: 'Medical Tape', price: 4.99, stock: 130 },
      { name: 'Antiseptic Wipes (50ct)', price: 7.49, stock: 140 },
      { name: 'First Aid Kit', price: 24.99, stock: 45 },
    ],
    'Health & Wellness': [
      { name: 'Blood Pressure Monitor', price: 49.99, stock: 35 },
      { name: 'Glucose Test Strips (50ct)', price: 32.99, stock: 55 },
      { name: 'Heating Pad Electric', price: 29.99, stock: 40 },
      { name: 'Compression Socks', price: 16.99, stock: 80 },
      { name: 'Sleep Aid Tablets', price: 14.99, stock: 95 },
    ],
    'Baby Care': [
      { name: 'Baby Diapers (Size 3)', price: 24.99, stock: 70 },
      { name: 'Baby Wipes (80ct)', price: 6.99, stock: 150 },
      { name: 'Baby Lotion 16oz', price: 8.99, stock: 90 },
      { name: 'Baby Powder', price: 5.99, stock: 110 },
      { name: 'Diaper Rash Cream', price: 7.99, stock: 100 },
    ]
  };

  const handlePOSClick = () => {
    setShowCart(!showCart); // Toggle the visibility of the current order
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
  };

  const handleAddToCart = (product) => {
    // Function to add product to cart (temporary implementation)
    alert(`${product.name} added to cart`);
  };

  const cartItems = [
    { name: 'Cough Syrup', price: 10.99, quantity: 1 },
    { name: 'Allergy Relief (Loratadine)', price: 12.99, quantity: 1 }
  ];

  const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const tax = subtotal * 0.12; // 12% tax
  const total = subtotal + tax;

  return (
    <div className="app">
      {/* Navbar */}
      <div className="navbar">
        <div className="navbar-title">PharmaCare Drugstore POS</div>
        <div className="navbar-buttons">
          <button
            className={activeButton === 'Dashboard' ? 'active' : ''}
            onClick={() => handleButtonClick('Dashboard')}
          >
            <img src={`./assets/icons/dashboard_${activeButton === 'Dashboard' ? 'CI' : 'NCI'}.png`} alt="Dashboard" />
            Dashboard
          </button>
          <button
            className={activeButton === 'POS' ? 'active' : ''}
            onClick={() => handleButtonClick('POS')}
          >
            <img src={`./assets/icons/POS_${activeButton === 'POS' ? 'CI' : 'NCI'}.png`} alt="POS" />
            POS
          </button>
          <button
            className={activeButton === 'History' ? 'active' : ''}
            onClick={() => handleButtonClick('History')}
          >
            <img src={`./assets/icons/history_${activeButton === 'History' ? 'CI' : 'NCI'}.png`} alt="History" />
            History
          </button>
        </div>
      </div>

      {/* Search Bar */}
      <div className="search-bar">
        <input type="text" placeholder="Search products by name..." />
      </div>

      {/* Category Tabs */}
      <div className="category-tabs">
        {['All', 'OTC Medications', 'Vitamins & Supplements', 'Personal Care', 'First Aid', 'Health & Wellness', 'Baby Care'].map((category) => (
          <button
            key={category}
            className={selectedCategory === category ? 'active' : ''}
            onClick={() => handleCategoryClick(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Product Display */}
      <div className="product-display">
        <div className="product-grid">
          {products[selectedCategory].map((product, index) => (
            <div className="product-card" key={index}>
              <img src={`./assets/images/${selectedCategory.toLowerCase().replace(/\s+/g, '')}.png`} alt="Product" />
              <h4>{product.name}</h4>
              <p>₱{product.price}</p>
              <p>Stock: {product.stock}</p>
              <button onClick={() => handleAddToCart(product)}>+ Add to Cart</button>
            </div>
          ))}
        </div>
      </div>

      {/* Cart Sidebar (Current Order) */}
      {showCart && (
        <div className="cart-sidebar">
          <h2>Current Order</h2>
          {cartItems.map((item, index) => (
            <div className="cart-item" key={index}>
              <div className="item-info">
                <span>{item.name}</span>
                <span>{item.price} each</span>
              </div>
              <div className="quantity-controls">
                <button>-</button>
                <span>{item.quantity}</span>
                <button>+</button>
              </div>
              <div className="remove-btn">X</div>
            </div>
          ))}

          <div className="subtotal">
            <p>Subtotal: ₱{subtotal.toFixed(2)}</p>
            <p>Tax (12%): ₱{tax.toFixed(2)}</p>
            <p>Total: ₱{total.toFixed(2)}</p>
          </div>
          <button className="checkout-button">Proceed to Payment</button>
        </div>
      )}
    </div>
  );
}

export default App;
