import React, { useState } from 'react';
import './App.css';

function App() {
  const [showCart, setShowCart] = useState(false);

  // Example cart item for testing
  const cartItems = [
    {
      id: 1,
      name: 'Cough Syrup',
      price: 10.99,
      quantity: 1,
    },
    {
      id: 2,
      name: 'Allergy Relief (Loratadine)',
      price: 12.99,
      quantity: 1,
    }
  ];

  // Calculate subtotal, tax, and total
  const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const tax = subtotal * 0.12; // 12% tax
  const total = subtotal + tax;

  const handlePOSClick = () => {
    setShowCart(!showCart); // Toggle the visibility of the current order
  };

  return (
    <div className="app">
      {/* Navbar */}
      <div className="navbar">
        <div className="navbar-title">PharmaCare Drugstore POS</div>
        <div className="navbar-buttons">
          <button onClick={handlePOSClick} className={showCart ? 'active' : ''}>
            POS
          </button>
        </div>
      </div>

      {/* Search bar */}
      <div className="search-bar">
        <input type="text" placeholder="Search products by name..." />
      </div>

      {/* Main content */}
      <div className="main-content">
        {/* Product Grid */}
        <div className="product-display">
          <div className="category-tabs">
            <button>All</button>
            <button>OTC Medications</button>
            <button>Vitamins & Supplements</button>
            <button>Personal Care</button>
            <button>First Aid</button>
            <button>Health & Wellness</button>
            <button>Baby Care</button>
          </div>

          <div className="product-grid">
            {/* Display products here (hardcoded for now) */}
            <div className="product-card">
              <img src={require('./assets/images/medicine.png')} alt="Medicine" />
              <h4>Mercury Acetaminophen 500mg</h4>
              <p>₱8.99</p>
              <button>+ Add to Cart</button>
            </div>
            {/* Add other product cards as needed */}
          </div>
        </div>

        {/* Show current order only when POS button is clicked */}
        {showCart && (
          <div className="cart-sidebar">
            <h2>Current Order</h2>
            {cartItems.map(item => (
              <div className="cart-item" key={item.id}>
                <div className="item-info">
                  <span>{item.name}</span>
                  <span>{item.price} each</span>
                </div>
                <div className="quantity-controls">
                  <button>-</button>
                  <span>{item.quantity}</span>
                  <button>+</button>
                </div>
                <div className="remove-btn">X</div>
              </div>
            ))}

            <div className="subtotal">
              <p>Subtotal: ₱{subtotal.toFixed(2)}</p>
              <p>Tax (12%): ₱{tax.toFixed(2)}</p>
              <p>Total: ₱{total.toFixed(2)}</p>
            </div>
            <button className="checkout-button">Proceed to Payment</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
