import React, { useState } from 'react';
import './App.css';

// Import Product Images
import medicineImg from './assets/images/medicine.png';
import vitaminsImg from './assets/images/vitamins&supplements.png';
import personalCareImg from './assets/images/personalcare.png';
import firstAidImg from './assets/images/firstaid.png';
import healthWellnessImg from './assets/images/health&wellness.png';
import babyCareImg from './assets/images/babycare.png';

// Import Icons
import dashboard_CI from './assets/images/dashboard_CI.png';
import dashboard_NCI from './assets/images/dashboard_NCI.png';
import POS_CI from './assets/images/POS_CI.png';
import POS_NCI from './assets/images/POS_NCI.png';
import history_CI from './assets/images/history_CI.png';
import history_NCI from './assets/images/history_NCI.png';
import searchIcon from './assets/images/search_icon.png';
import deleteIcon from './assets/images/delete_icon.png';

const App = () => {
  const [cart, setCart] = useState([
    { id: 4, name: 'Allergy Relief (Loratadine)', price: 12.99, quantity: 4, category: 'OTC Medications' }
  ]);
  const [searchQuery, setSearchQuery] = useState('');
  const [activeCategory, setActiveCategory] = useState('OTC Medications');
  const [activeTab, setActiveTab] = useState('POS');
  
  // Payment Modal States
  const [isPaymentModalOpen, setIsPaymentModalOpen] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState(null); 
  const [cashReceived, setCashReceived] = useState('');
  const [paymentStatus, setPaymentStatus] = useState('idle'); // 'idle', 'success'

  const products = [
    { id: 1, name: 'Acetaminophen 500mg', price: 8.99, image: medicineImg, stock: 150, category: 'OTC Medications' },
    { id: 2, name: 'Ibuprofen 200mg', price: 9.49, image: medicineImg, stock: 200, category: 'OTC Medications' },
    { id: 3, name: 'Aspirin 81mg', price: 7.99, image: medicineImg, stock: 180, category: 'OTC Medications' },
    { id: 4, name: 'Allergy Relief (Loratadine)', price: 12.99, image: medicineImg, stock: 120, category: 'OTC Medications' },
    { id: 5, name: 'Cough Syrup', price: 10.99, image: medicineImg, stock: 85, category: 'OTC Medications' },
    { id: 6, name: 'Antacid Tablets', price: 8.49, image: medicineImg, stock: 160, category: 'OTC Medications' },
    { id: 7, name: 'Cold & Flu Relief', price: 11.99, image: medicineImg, stock: 95, category: 'OTC Medications' },
    { id: 8, name: 'Multivitamin Daily', price: 15.99, image: vitaminsImg, stock: 140, category: 'Vitamins & Supplements' },
    { id: 37, name: 'Diaper Rash Cream', price: 7.99, image: babyCareImg, stock: 100, category: 'Baby Care' },
  ];

  const categories = ['All', 'OTC Medications', 'Vitamins & Supplements', 'Personal Care', 'First Aid', 'Health & Wellness', 'Baby Care'];

  const filteredProducts = products.filter(p => 
    (activeCategory === 'All' || p.category === activeCategory) &&
    p.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const addToCart = (product) => {
    const exists = cart.find(item => item.id === product.id);
    if (exists) {
      setCart(cart.map(item => item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const updateQty = (id, delta) => {
    setCart(cart.map(item => item.id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item));
  };

  const subtotal = cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
  const tax = subtotal * 0.12;
  const total = subtotal + tax;

  const closePaymentModal = () => {
    setIsPaymentModalOpen(false);
    setPaymentMethod(null);
    setCashReceived('');
    setPaymentStatus('idle');
    if (paymentStatus === 'success') setCart([]); // Clear cart after successful transaction
  };

  const handleCompletePayment = () => {
    setPaymentStatus('success');
  };

  return (
    <div className="pos-container">
      <header className="main-nav">
        <h1 className="logo">PharmaCare Drugstore POS</h1>
        <div className="nav-actions">
          <button className={activeTab === 'Dashboard' ? 'nav-btn active' : 'nav-btn'} onClick={() => setActiveTab('Dashboard')}>
            <img src={activeTab === 'Dashboard' ? dashboard_CI : dashboard_NCI} alt="" /> Dashboard
          </button>
          <button className={activeTab === 'POS' ? 'nav-btn active' : 'nav-btn'} onClick={() => setActiveTab('POS')}>
            <img src={activeTab === 'POS' ? POS_CI : POS_NCI} alt="" /> POS
          </button>
          <button className={activeTab === 'History' ? 'nav-btn active' : 'nav-btn'} onClick={() => setActiveTab('History')}>
            <img src={activeTab === 'History' ? history_CI : history_NCI} alt="" /> History
          </button>
        </div>
      </header>

      <main className="pos-content">
        <div className="inventory-section">
          <div className="search-box">
            <img src={searchIcon} alt="" className="search-icon-img" />
            <input 
              type="text" 
              placeholder="Search products by name..." 
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>

          <div className="category-bar">
            {categories.map(cat => (
              <button 
                key={cat} 
                className={activeCategory === cat ? 'cat-btn active' : 'cat-btn'}
                onClick={() => setActiveCategory(cat)}
              >
                {cat}
              </button>
            ))}
          </div>

          <div className="product-grid">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card">
                <div className="img-container">
                  <img src={product.image} alt={product.name} />
                </div>
                <h3>{product.name}</h3>
                <p className="cat-label">{product.category}</p>
                <div className="card-footer">
                   <div>
                    <span className="price">${product.price.toFixed(2)}</span>
                    <span className="stock">Stock: {product.stock}</span>
                   </div>
                   <button className="add-btn" onClick={() => addToCart(product)}>+</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <aside className="order-sidebar">
          <div className="sidebar-header">
            <h2>Current Order</h2>
            <button className="clear-all" onClick={() => setCart([])}>Clear All</button>
          </div>
          <p className="item-count">{cart.length} items</p>

          <div className="cart-list">
            {cart.map(item => (
              <div key={item.id} className="cart-item">
                <div className="item-details">
                  <h4>{item.name}</h4>
                  <p className="item-price-each">${item.price.toFixed(2)} each</p>
                  <div className="qty-controls">
                    <button onClick={() => updateQty(item.id, -1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => updateQty(item.id, 1)}>+</button>
                  </div>
                </div>
                <div className="item-total-section">
                   <button className="delete-item" onClick={() => setCart(cart.filter(i => i.id !== item.id))}>
                     <img src={deleteIcon} alt="Delete" />
                   </button>
                   <p className="item-total">${(item.price * item.quantity).toFixed(2)}</p>
                </div>
              </div>
            ))}
          </div>

          <div className="billing-summary">
            <div className="bill-row"><span>Subtotal:</span> <span>${subtotal.toFixed(2)}</span></div>
            <div className="bill-row"><span>Tax (12%):</span> <span>${tax.toFixed(2)}</span></div>
            <hr />
            <div className="bill-row total"><span>Total:</span> <span>${total.toFixed(2)}</span></div>
            <button className="pay-btn" onClick={() => setIsPaymentModalOpen(true)}>Proceed to Payment</button>
          </div>
        </aside>
      </main>

      {/* Payment Modal */}
      {isPaymentModalOpen && (
        <div className="modal-overlay">
          {paymentStatus === 'success' ? (
            <div className="success-modal">
              <div className="success-icon">âœ“</div>
              <h2>Payment Successful!</h2>
              <p>Transaction completed</p>
              <button className="close-success-btn" onClick={closePaymentModal}>Back to POS</button>
            </div>
          ) : (
            <div className="payment-modal">
              <div className="modal-header">
                <h2>Payment</h2>
                <button className="close-modal" onClick={closePaymentModal}>âœ•</button>
              </div>
              
              <div className="amount-display">
                <p>Total Amount</p>
                <h1>${total.toFixed(2)}</h1>
              </div>

              {!paymentMethod ? (
                <div className="payment-options">
                  <p className="section-label">Select Payment Method</p>
                  <div className="method-grid">
                    <button className="method-item" onClick={() => setPaymentMethod('cash')}>
                      <span className="method-icon">ðŸ’µ</span>
                      <span>Cash</span>
                    </button>
                    <button className="method-item" onClick={() => setPaymentMethod('card')}>
                      <span className="method-icon">ðŸ’³</span>
                      <span>Card</span>
                    </button>
                    <button className="method-item" onClick={() => setPaymentMethod('mobile')}>
                      <span className="method-icon">ðŸ“±</span>
                      <span>Mobile</span>
                    </button>
                  </div>
                </div>
              ) : paymentMethod === 'cash' ? (
                <div className="cash-payment-view">
                  <div className="view-header">
                    <p className="section-label">Cash Payment</p>
                    <button className="change-method" onClick={() => setPaymentMethod(null)}>Change Method</button>
                  </div>
                  <div className="input-group">
                    <label>Cash Received</label>
                    <input type="number" placeholder="0.00" value={cashReceived} onChange={(e) => setCashReceived(e.target.value)} />
                  </div>
                  <div className="quick-amounts">
                    <p className="section-label-sm">Quick Amount</p>
                    <div className="quick-grid">
                      <button onClick={() => setCashReceived(total.toFixed(2))}>${total.toFixed(2)}</button>
                      <button onClick={() => setCashReceived('80')}>$80</button>
                      <button onClick={() => setCashReceived('100')}>$100</button>
                    </div>
                  </div>
                  <div className="modal-actions">
                    <button className="cancel-btn" onClick={closePaymentModal}>Cancel</button>
                    <button 
                      className={`complete-btn ${parseFloat(cashReceived) >= total ? 'active' : ''}`} 
                      disabled={parseFloat(cashReceived) < total || !cashReceived}
                      onClick={handleCompletePayment}
                    >
                      Complete Payment
                    </button>
                  </div>
                </div>
              ) : (
                <div className="payment-method-view">
                  <div className="view-header">
                    <p className="section-label">{paymentMethod === 'card' ? 'Card Payment' : 'Mobile Payment'}</p>
                    <button className="change-method" onClick={() => setPaymentMethod(null)}>Change Method</button>
                  </div>
                  <div className="reader-status-box">
                    <div className="reader-icon">{paymentMethod === 'card' ? 'ðŸ’³' : 'ðŸ“±'}</div>
                    <p className="main-prompt">{paymentMethod === 'card' ? 'Please insert or tap card' : 'Scan QR code or tap device'}</p>
                    <p className="sub-prompt">{paymentMethod === 'card' ? 'Waiting for card reader...' : 'Apple Pay, Google Pay, Samsung Pay'}</p>
                  </div>
                  <div className="modal-actions">
                    <button className="cancel-btn" onClick={closePaymentModal}>Cancel</button>
                    <button className="complete-btn active" onClick={handleCompletePayment}>Complete Payment</button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
