// src/App.js
import React, { useState } from 'react';

const App = () => {
  // Hardcoded items for the cart
  const [items] = useState([
    { name: 'Product 1', price: 100 },
    { name: 'Product 2', price: 200 },
    { name: 'Product 3', price: 150 }
  ]);

  const [receiptNumber, setReceiptNumber] = useState(null);

  // Calculate the subtotal (total)
  const total = items.reduce((sum, item) => sum + item.price, 0);

  // Handle complete sale
  const handleCompleteSale = () => {
    const receipt = `#${Math.random().toString().slice(2, 8)}`; // Generate a random receipt number
    setReceiptNumber(receipt); // Set the receipt number
  };

  return (
    <div className="checkout-container">
      <h1 className="checkout-title">Checkout</h1>
      
      {/* Item List */}
      <ul className="item-list">
        {items.map((item, index) => (
          <li key={index} className="item">
            <span>{item.name}</span> - ₱{item.price}
          </li>
        ))}
      </ul>

      {/* Subtotal */}
      <div className="subtotal">
        <h3>Total: ₱{total}</h3>
      </div>

      {/* Complete Sale Button */}
      <button className="checkout-button" onClick={handleCompleteSale}>
        Complete Sale
      </button>

      {/* Receipt Number */}
      {receiptNumber && (
        <div className="success-message">
          <h4>Sale Completed!</h4>
          <p>Receipt Number: <strong>{receiptNumber}</strong></p>
        </div>
      )}
    </div>
  );
};

export default App;
