import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [cart, setCart] = useState([]);

  const categories = [
    'All', 'OTC Medications', 'Vitamins & Supplements', 'Personal Care', 'First Aid', 'Health & Wellness', 'Baby Care'
  ];

  const products = {
    'OTC Medications': [
      { name: 'Acetaminophen 500mg', price: '$8.99', stock: 150 },
      { name: 'Ibuprofen 200mg', price: '$9.49', stock: 200 },
      { name: 'Aspirin 81mg', price: '$7.99', stock: 180 },
      { name: 'Allergy Relief (Loratadine)', price: '$12.99', stock: 120 },
      { name: 'Cough Syrup', price: '$10.99', stock: 85 },
      { name: 'Antacid Tablets', price: '$8.49', stock: 160 },
      { name: 'Cold & Flu Relief', price: '$11.99', stock: 95 },
    ],
    // Other categories will follow the same structure...
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const calculateTotal = () => {
    const subtotal = cart.reduce((total, product) => total + parseFloat(product.price.slice(1)), 0);
    const tax = subtotal * 0.12;
    return { subtotal, tax, total: subtotal + tax };
  };

  return (
    <div className="App">
      <header className="navbar">
        <button className="nav-button active">
          <img src="dashboard_CI.png" alt="Dashboard" />
          Dashboard
        </button>
        <button className="nav-button">
          <img src="POS_NCI.png" alt="POS" />
          POS
        </button>
        <button className="nav-button">
          <img src="history_NCI.png" alt="History" />
          History
        </button>
      </header>

      <div className="pos-container">
        <div className="left-panel">
          <div className="search-bar">
            <input type="text" placeholder="Search products by name..." />
          </div>

          <div className="category-buttons">
            {categories.map((category) => (
              <button
                key={category}
                className={selectedCategory === category ? 'active' : ''}
                onClick={() => handleCategoryClick(category)}
              >
                {category}
              </button>
            ))}
          </div>

          <div className="product-grid">
            {selectedCategory && products[selectedCategory] && products[selectedCategory].map((product, index) => (
              <div key={index} className="product-card">
                <img src={`${product.name}.png`} alt={product.name} />
                <div className="product-details">
                  <h3>{product.name}</h3>
                  <p>{product.price}</p>
                  <p>Stock: {product.stock}</p>
                  <button onClick={() => addToCart(product)}>+</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="right-panel">
          <h3>Current Order</h3>
          {cart.length > 0 ? (
            <>
              {cart.map((item, index) => (
                <div key={index} className="cart-item">
                  <span>{item.name}</span>
                  <span>{item.price}</span>
                  <button onClick={() => setCart(cart.filter((_, i) => i !== index))}>Delete</button>
                </div>
              ))}
              <div className="cart-summary">
                <p>Subtotal: ${calculateTotal().subtotal.toFixed(2)}</p>
                <p>Tax (12%): ${calculateTotal().tax.toFixed(2)}</p>
                <p>Total: ${calculateTotal().total.toFixed(2)}</p>
              </div>
              <button className="proceed-to-payment">Proceed to Payment</button>
            </>
          ) : (
            <p>No items in the cart.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
