// src/App.js
import React, { useState } from 'react';
import './App.css';
import medicineImage from './assets/images/medicine.png'; // Import your image

const App = () => {
  const [cart, setCart] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  const products = [
    { id: 1, name: 'Mercury Acetaminophen 500mg', price: 8.99, image: medicineImage, stock: 150 },
    { id: 2, name: 'Mercury Ibuprofen 200mg', price: 9.49, image: medicineImage, stock: 200 },
    { id: 3, name: 'Mercury Aspirin 81mg', price: 7.99, image: medicineImage, stock: 180 },
    { id: 4, name: 'Mercury Allergy Relief (Loratidine)', price: 12.99, image: medicineImage, stock: 120 },
    { id: 5, name: 'Mercury Cough Syrup', price: 10.99, image: medicineImage, stock: 85 },
  ];

  // Add item to the cart
  const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      existingItem.quantity += 1;
      setCart([...cart]);
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  // Calculate totals
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const tax = subtotal * 0.12;
  const total = subtotal + tax;

  // Handle removing item from cart
  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  // Handle search query change
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  return (
    <div className="app">
      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search products by name..."
          value={searchQuery}
          onChange={handleSearchChange}
        />
      </div>

      {/* Product Display Section */}
      <div className="product-display">
        <h2>Products</h2>
        <div className="product-grid">
          {products
            .filter(product => product.name.toLowerCase().includes(searchQuery.toLowerCase())) // Search filtering
            .map(product => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.name} className="product-image" />
                <h4>{product.name}</h4>
                <p>‚Ç±{product.price.toFixed(2)}</p>
                <button className="add-to-cart" onClick={() => addToCart(product)}>
                  + Add to Cart
                </button>
              </div>
            ))}
        </div>
      </div>

      {/* Cart Sidebar */}
      <div className="cart-sidebar">
        <h2>Current Order</h2>
        {cart.length > 0 ? (
          <div className="cart-items">
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                <div className="item-info">
                  <span>{item.name} - ‚Ç±{item.price.toFixed(2)}</span>
                  <div className="quantity-controls">
                    <button onClick={() => changeQuantity(item.id, 'decrease')}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => changeQuantity(item.id, 'increase')}>+</button>
                  </div>
                </div>
                <span>‚Ç±{(item.price * item.quantity).toFixed(2)}</span>
                <button className="remove-btn" onClick={() => removeFromCart(item.id)}>üóëÔ∏è</button>
              </div>
            ))}
          </div>
        ) : (
          <p>Your cart is empty.</p>
        )}

        <div className="subtotal">
          <p>Subtotal: ‚Ç±{subtotal.toFixed(2)}</p>
          <p>Tax (12%): ‚Ç±{tax.toFixed(2)}</p>
          <p>Total: ‚Ç±{total.toFixed(2)}</p>
        </div>

        <button className="checkout-button" onClick={() => alert('Proceeding to payment')}>
          Proceed to Payment
        </button>
      </div>
    </div>
  );
};

export default App;
