import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const categories = [
    { name: 'OTC Medication', image: 'medicine.png' },
    { name: 'Vitamins & Supplements', image: 'vitamins&supplements.png' },
    { name: 'Personal Care', image: 'personalcare.png' },
    { name: 'First Aid', image: 'firstaid.png' },
    { name: 'Health & Wellness', image: 'health&wellness.png' },
    { name: 'Baby Care', image: 'babycare.png' },
  ];

  const products = {
    'OTC Medication': [
      { name: 'Acetaminophen 500mg', price: '$8.99', stock: 150 },
      { name: 'Ibuprofen 200mg', price: '$9.49', stock: 200 },
      { name: 'Aspirin 81mg', price: '$7.99', stock: 180 },
      { name: 'Allergy Relief (Loratadine)', price: '$12.99', stock: 120 },
      { name: 'Cough Syrup', price: '$10.99', stock: 85 },
      { name: 'Antacid Tablets', price: '$8.49', stock: 160 },
      { name: 'Cold & Flu Relief', price: '$11.99', stock: 95 },
    ],
    'Vitamins & Supplements': [
      { name: 'Multivitamin Daily', price: '$15.99', stock: 140 },
      { name: 'Vitamin D3 2000 IU', price: '$12.99', stock: 110 },
      { name: 'Vitamin C 1000mg', price: '$13.49', stock: 130 },
      { name: 'Omega-3 Fish Oil', price: '$18.99', stock: 90 },
      { name: 'Calcium + Vitamin D', price: '$14.99', stock: 105 },
      { name: 'Probiotic Complex', price: '$24.99', stock: 75 },
    ],
    // Add other categories and products in a similar structure...
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="App">
      <header className="navbar">
        <button className="nav-button active">
          <img src="dashboard_CI.png" alt="Dashboard" />
          Dashboard
        </button>
        <button className="nav-button">
          <img src="POS_NCI.png" alt="POS" />
          POS
        </button>
        <button className="nav-button">
          <img src="history_NCI.png" alt="History" />
          History
        </button>
      </header>

      {selectedCategory && (
        <div className="products">
          <h2>{selectedCategory}</h2>
          {products[selectedCategory].map((product, index) => (
            <div key={index} className="product">
              <img src={`${product.name}.png`} alt={product.name} />
              <div className="product-details">
                <h3>{product.name}</h3>
                <p>{product.price}</p>
                <p>Stock: {product.stock}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
