// src/App.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  // Hardcoded product list (can be replaced with API in the future)
  const [products] = useState([
    { id: 1, name: 'Mercury Acetaminophen 500mg', price: 8.99, stock: 150 },
    { id: 2, name: 'Mercury Ibuprofen 200mg', price: 9.49, stock: 200 },
    { id: 3, name: 'Mercury Aspirin 81mg', price: 7.99, stock: 180 },
    { id: 4, name: 'Mercury Allergy Relief (Loratidine)', price: 12.99, stock: 120 },
    { id: 5, name: 'Mercury Cough Syrup', price: 10.99, stock: 85 },
    // Add more products as needed
  ]);

  const [cart, setCart] = useState([]);

  // Add item to the cart
  const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      existingItem.quantity += 1; // Increase quantity if item exists
      setCart([...cart]); // Update cart
    } else {
      setCart([...cart, { ...product, quantity: 1 }]); // Add new item to cart
    }
  };

  // Calculate totals
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const tax = subtotal * 0.12; // 12% tax
  const total = subtotal + tax;

  // Handle removing item from cart
  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  return (
    <div className="checkout-container">
      {/* Product Display Section */}
      <div className="product-display">
        <h2>Products</h2>
        <div className="product-grid">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <img
                src="https://via.placeholder.com/100" // Placeholder image for the product
                alt={product.name}
                className="product-image"
              />
              <div className="product-info">
                <h4>{product.name}</h4>
                <p>‚Ç±{product.price.toFixed(2)}</p>
                <button onClick={() => addToCart(product)}>+ Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Cart Sidebar */}
      <div className="cart-sidebar">
        <h2>Current Order</h2>
        {cart.length > 0 ? (
          <div className="cart-items">
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                <span>{item.name} - ‚Ç±{item.price.toFixed(2)}</span>
                <div className="quantity-controls">
                  <button onClick={() => addToCart(item)} className="increase-btn">+</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => removeFromCart(item.id)} className="remove-btn">üóëÔ∏è</button>
                </div>
                <span>‚Ç±{(item.price * item.quantity).toFixed(2)}</span>
              </div>
            ))}
          </div>
        ) : (
          <p>Your cart is empty.</p>
        )}

        <div className="subtotal">
          <p>Subtotal: ‚Ç±{subtotal.toFixed(2)}</p>
          <p>Tax (12%): ‚Ç±{tax.toFixed(2)}</p>
          <p>Total: ‚Ç±{total.toFixed(2)}</p>
        </div>

        <button className="checkout-button" onClick={() => alert('Proceeding to payment')}>
          Proceed to Payment
        </button>
      </div>
    </div>
  );
};

export default App;
